<?php		$current_options=get_option('spa_theme_options');	if(isset($_POST['spasalon_settings_save_2']))	{			if($_POST['spasalon_settings_save_2'] == 1) 	{		/*nonce field implement*/		if ( empty($_POST) || !wp_verify_nonce($_POST['spa_gernalsetting_nonce_customization'],'spa_customization_nonce_gernalsetting') )		{		   print 'Sorry, your nonce did not verify.';		   exit;		}		else 		{			$current_options['spa_Site_Title']['site_title_px']=sanitize_text_field($_POST['site_title_px']);			$current_options['spa_Site_Title']['color_title']=sanitize_text_field($_POST['color_title']);			$current_options['spa_Site_Title']['font_family']=sanitize_text_field($_POST['font_family']);			$current_options['spa_Site_Title']['font_style']=sanitize_text_field($_POST['font_style']);			$current_options['checkbox_title']=sanitize_text_field($_POST['checkbox_title']);			$current_options['contactus_title']=sanitize_text_field($_POST['contactus_title']);			$current_options['contactus_description']=sanitize_text_field($_POST['contactus_description']);			$current_options['upload_image']=sanitize_text_field($_POST['upload_image']);		    $current_options['height']=sanitize_text_field($_POST['height']);		    $current_options['width']=sanitize_text_field($_POST['width']);			$current_options['upload_image_favicon']=sanitize_text_field($_POST['upload_image_favicon']);			$current_options['posts_per_page']=sanitize_text_field($_POST['posts_per_page']);	         $current_options['map']=sanitize_text_field($_POST['map']);					update_option('spa_theme_options' , $current_options);					}     }	if($_POST['spasalon_settings_save_2'] == 2) 	 {		do_action('spasalon_restore_data', '2');			 } }  ?><form method="post" action = ""  id="theme_options_home_2">	 <?php wp_nonce_field('spa_customization_nonce_gernalsetting','spa_gernalsetting_nonce_customization'); ?>	<div class="postbox" id="spasalon_footer_custmization">		<div title="Click to toggle" class="handlediv"><br></